(1776, 87, 'es', 1, '<strong>Hay valores en conflicto para las constantes VHOST y SUBDOMAIN_INSTALL.</strong> Se asumirá que el valor de la configuración de subdominio es SUBDOMAIN_INSTALL.', NULL, '2014-08-08 18:53:53'),
(1777, 88, 'es', 1, '<strong>No podemos encontrar el sitio <code>%1$s</code>.</strong> Buscamos la tabla <code>%2$s</code>en la base de datos <code>%3$s</code>. ¿Es correcto?', NULL, '2014-08-08 18:53:53'),
